// SPDX-License-Identifier: MIT OR Apache-2.0

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";

/**
 * @notice Wraps the default admin role from OpenZeppelin's AccessControl for easy integration.
 */
abstract contract MinterRole is AccessControlUpgradeable {
  bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
  event MinterGranted(address admin);
  event MinterRevoked(address admin);

  modifier onlyMinter() {
    require(hasRole(MINTER_ROLE, msg.sender), "MinterRole: caller does not have the Minter role");
    _;
  }

  function isMinter() public view returns (bool) {
    return hasRole(MINTER_ROLE, _msgSender());
  }

  function grantMinter(address account) public {
    grantRole(MINTER_ROLE, account);
    emit MinterGranted(account);
  }

  function revokeMinter(address account) public {
    revokeRole(MINTER_ROLE, account);
    emit MinterRevoked(account);
  }

  uint256[1000] private __gap;
}
