// SPDX-License-Identifier: MIT OR Apache-2.0

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";

/**
 * @notice A place for common modifiers and functions used by various NFT721 mixins, if any.
 * @dev This also leaves a gap which can be used to add a new mixin to the top of the inheritance tree.
 */
abstract contract NFT721Core is ERC721Upgradeable{
  
  event AmountTransferred(
    address toAddress,
    address fromAddress,
    uint256 amount
  );

  function transferToWallet(address receiverUserAddress) public payable {
    require(msg.value > 0, 'Amount must be greater than 0');
    payable(receiverUserAddress).transfer(msg.value);
    emit AmountTransferred(receiverUserAddress, msg.sender, msg.value);
  }

  uint256[900] private ______gap;
}
