// SPDX-License-Identifier: MIT OR Apache-2.0

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";

/**
 * @notice Wraps the default admin role from OpenZeppelin's AccessControl for easy integration.
 */
abstract contract OperatorRole is AccessControlUpgradeable {
  bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
  event OperatorGranted(address admin);
  event OperatoRevoked(address admin);

  modifier onlyOperator() {
    require(hasRole(OPERATOR_ROLE, msg.sender), "OperatorRole: caller does not have the Operator role");
    _;
  }

  function isOperator() public view returns (bool) {
    return hasRole(OPERATOR_ROLE, _msgSender());
  }

  function grantOperator(address account) public {
    grantRole(OPERATOR_ROLE, account);
    emit OperatorGranted(account);
  }

  function revokeOperator(address account) public {
    revokeRole(OPERATOR_ROLE, account);
    emit OperatoRevoked(account);
  }

  uint256[1000] private __gap;
}
